const express = require('express');
const cors = require('cors');

const app = express(),
      port = 3080;
    
app.use(cors());

app.get("/Livres", (req, res) => {
    res.json(
        [{
            "name" : "Harry Potter 1",
            "price" : "7",
            "author" : "J.K Rowling"
        }]
    )
});

app.listen(port, () => {
    console.log('Server is running on port 3080');
})